from base import LeetCodeProblem


class Problem(LeetCodeProblem):
    # for behaviours other than exact match between solution output and expected output
    # see # Testers in README.md
    """
    https://leetcode.com/problems/substring-with-concatenation-of-all-words/

    sliding window + match words recursively
    """
    tester = "all"

    def get_tests(self):
        # return test cases here
        return (
            # each test case has the following structure
            # ( parameter , expected output ),
            # # OR # #
            # ( (param1, param2), expected output ),
            (("", []), []),
            (("wordgoodgoodgoodbestword", ["word","good","best","word"]), []),
            (("barfoothefoobarman", ["foo", "bar"]), [0, 9]),
            (("barfoofoobarthefoobarman", ["foo", "bar", "the"]), [6, 9, 12]),
            (("", ["ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba"]), []),
        )

    def solution(self, s, words):
        # have fun ~ ^_^
        def can_match(__string: str, __words: dict) -> bool:
            # note that this function can only return true when __string exactly match words
            # first we compute some descriptive stats on words
            longest_word = 0
            total_chars = 0
            for each_word, count in __words.items():
                if len(each_word) > longest_word:
                    longest_word = len(each_word)
                total_chars += len(each_word) * count

            # bail out early if matching is impossible
            if len(__string) < total_chars:
                return False
            elif total_chars == 0:  # <= matching finished, yay
                return True

            # then we do a sliding window and find match in words
            pointer = 1
            while pointer <= longest_word:
                selected_word = __string[:pointer]
                if selected_word in __words and __words[selected_word] > 0:
                    # call self with the resting of string and words
                    new_words = __words.copy()
                    new_words[selected_word] -= 1

                    if can_match(__string[pointer:], new_words):
                        return True
                    else:
                        pointer += 1
                else:
                    pointer += 1
            # finished iteration, no match found
            return False

        # make a mapping of words and count
        dictionary = {}
        for each_word in words:
            try:
                dictionary[each_word] += 1
            except KeyError:
                dictionary[each_word] = 1

        # iterate through source string
        found_answer = []
        for i in range(len(s)):
            if can_match(s[i:], dictionary):
                found_answer.append(i)

        return found_answer


# instanciate your Problem class and run
prob = Problem()
prob.run()
